import java.util.*;public class sec{    public static void main(String args[]){        Scanner sc = new Scanner(System.in);        int[] arr[]= new int[5][5];        int a=-1,b=-1;        for(int i=0;i<5;i++){            for(int j=0;j<5;j++){                arr[i][j] = sc.nextInt();                if(arr[i][j] == 1){                    a = i+1;                    b = j+1;                }            }        }        int c = Math.abs(a-3)+Math.abs(b-3);        System.out.println(c);    }}